  },
  {
    id: 7,
    text: "Reading books and articles on various subjects can help expand your knowledge and vocabulary.",
  },
  {
    id: 8,
    text: "A healthy mind and body are essential for maintaining a balanced lifestyle and well-being.",
  },
  {
    id: 9,
    text: "Success is not final, failure is not fatal: It is the courage to continue that counts in life.",
  },
  {
    id: 10,
    text: "By focusing on continuous improvement, you can achieve long-term goals and reach your dreams.",
  },
];

export default function Main() {
  const [position, setPositoin] = useState(0);
  const [count, setCount] = useState(0);
  const [initial, setInitial] = useState(0);
  const [time, setTime] = useState(0);
  const [selected, setSelected] = useState(null);
  const [level, setLevel] = useState(null);
  const [started, setStarted] = useState(false);
  const [str, setStr] = useState(null);
  const [words, SetWords] = useState(0);

  useEffect(
    function () {
      const random = Math.floor(Math.random() * 10);

      if (level === "easy") {
        setStr(easySamples[random].text);
      } else if (level === "medium") {
        setStr(mediumSamples[random].text);
      } else if (level === "hard") {
        setStr(hardSamples[random].text);
      }
    },
    [level]
  );

  useEffect(function () {
    function handleKeyPress(e) {
      if (level !== null) {
        e.code === "Space" && e.preventDefault();
        str.charCodeAt(position) === 32 && SetWords((words) => words + 1);

        if (position === 0) {
          const date = new Date();
          console.log(initial);
          setInitial(() => {
            const curTime = date.getMinutes() + date.getSeconds() / 60;
            return curTime;
          });
        }

        if (e.keyCode === str.charCodeAt(position) && position < str.length) {
          setCount((count) => count + 1);
        }

        if (position < str.length-1) {
          setSelected(e.keyCode);
          const date = new Date();
          setTime(() => {
            const curTime = date.getMinutes() + date.getSeconds() / 60;
            return curTime - initial;
          });
          setPositoin((position) => position + 1);
        } else {
          setSelected(null);
          setLevel(null);
          setStarted(false);
        }
      }
    }

    document.addEventListener("keypress", handleKeyPress);

    return function () {
      document.removeEventListener("keypress", handleKeyPress);
    };
  });

  return (
    <div className="main">
      {!started && !str && (
        <>
          <Start1 setStarted={setStarted} />
        </>
      )}
      {started && !level && <Level setLevel={setLevel} />}
      {level !== null && (
        <>
          <String str={str} position={position} />
          <p>{time === 0 ? time : words / time} WPM</p>
          <p>
            {position === 0 ? position : (count / position).toFixed(2) * 100}
          </p>
          <KeyBoard selected={selected} />
        </>
      )}
      {level === null && str !== null && <Results words={words} />}
    </div>

    // <div>

    // </div>
  );
}
