import React from "react";
import ReactDOM from "react-dom/client";
import reportWebVitals from "./reportWebVitals";
import { useState } from "react";
import "./home.css";

const coffeeMenu = [
  {
    id: 1,
    coffeeName: "Espresso",
    price: "5$",
    ingredients: "Espresso shot",
    soldOut: false,
  },
  {
    id: 2,
    coffeeName: "Latte",
    price: "8$",
    ingredients: "Espresso shot, Steamed Milk",
    soldOut: false,
  },
  {
    id: 3,
    coffeeName: "Cappuccino",
    price: "6$",
    ingredients: "Espresso, Steamed Milk, Milk Foam",
    soldOut: false,
  },
  {
    id: 4,
    coffeeName: "Macchiato",
    price: "5$",
    ingredients: "Espresso, Milk Foam",
    soldOut: true,
  },
  {
    id: 5,
    coffeeName: "Mocha",
    price: "7$",
    ingredients: "Espresso, Chocolate Syrup, Steamed Milk, Whipped Cream",
    soldOut: false,
  },
  {
    id: 6,
    coffeeName: "Flat White",
    price: "5$",
    ingredients: "Espresso, Microfoam",
    soldOut: false,
  },
  {
    id: 7,
    coffeeName: "Americano",
    price: "6$",
    ingredients: "Espresso, Hot Water",
    soldOut: false,
  },
  {
    id: 8,
    coffeeName: "Irish Coffee",
    price: "8$",
    ingredients: "Espresso, Whiskey, Sugar, Whipped Cream",
    soldOut: false,
  },
];

function App() {
  return (
    <>
      <Header />
      <Menu />
      <Footer />
    </>
  );
}

function Header() {
  return (
    <header>
      <div className="logo">Coffee Day</div>
      <div className="nav-buttons">
        <a className="button secondary-button" href="/">
          LOG IN
        </a>
        <a className="button primary-button " href="/">
          SIGN UP
        </a>
      </div>
    </header>
  );
}

function Menu() {
  const availableCoffees = coffeeMenu.length;
  return (
    <div className="container">
      {availableCoffees > 0 ? (
        <div className="coffee-grid">
          {coffeeMenu.map((ele) => (
            <Item
              coffeeName={ele.coffeeName}
              ingredients={ele.ingredients}
              price={ele.price}
              soldOut={ele.soldOut}
            />
          ))}
        </div>
      ) : (
        <div
          className="coffee-item"
          style={{ width: "fit-content", marginInline: "auto" }}
        >
          We're still working on our menu.Please come back later...!
        </div>
      )}
    </div>
  );
}

function Item(props) {
  // let [isOrdered, setOrder] = useState(false);
  // function set() {
  //   setOrder(!isOrdered);
  // }

  const [selectedId, setId] = useState(null);
  function handleMouseEnter() {
    setId(props.id);
  }

  function handleMouseLeave() {
    setId(null);
  }

  if (selectedId !== props.id) {
    return (
      <div
        className={`coffee-item ${props.soldOut ? "sold-out" : ""}`}
        onMouseEnter={handleMouseEnter}
      >
        <img
          src="./assets/coffee_cup.png"
          alt={props.coffeeName}
          className="coffee-icon-default card-text"
        ></img>
        <div className="coffee-name  card-text" style={{fontSize}}>{props.coffeeName}</div>
        <div className="coffee-ingredients  card-text">{props.ingredients}</div>
        {/* <div style={{ display: "flex", justifyContent: "center" }}>
      {props.soldOut ? (
        <p style={{ marginTop: "5px", fontWeight: "bolder", color: "#222" }}>
          Sold out
        </p>
      ) : (
        <button
          className="button primary-button"
          style={{ marginTop: "5px" }}
          // onClick={set}
        >
          Order Now
        </button>
      )}
    </div> */}
        {/* {isOrdered && <Modal coffeeObj={props} />} */}
      </div>
    );
  } else {
    return (
      <div
        className={`coffee-item ${props.soldOut ? "sold-out" : ""}`}
        onMouseLeave={handleMouseLeave}
      >
        <img
          src="./assets/coffee_cup.png"
          alt={props.coffeeName}
          className="coffee-icon-reverse card-text"
        ></img>

        {/* <div className="coffee-name card-text">{props.coffeeName}</div>
        <div className="coffee-ingredients card-text">{props.ingredients}</div>
        <div className="card-text" style={{ display: "flex", justifyContent: "center" }}>
          {props.soldOut ? (
            <p
              style={{ marginTop: "5px", fontWeight: "bolder", color: "#222" }}
            >
              Sold out
            </p>
          ) : (
            <button
              className="button primary-button card-text"
              style={{ marginTop: "5px" }}
            >
              Order Now
            </button>
          )}
        </div> */}

        <div className="modal-cont">
          <div className="coffee-name">
            <span style={{ fontSize: "1rem" }}>
              <b>Item-Name :</b>
            </span>{" "}
            {props.coffeeName}
          </div>
          <div className="coffee-ingredients">
            <span style={{ fontSize: "1rem" }}>
              <b>Ingredients :</b>
            </span>{" "}
            {props.ingredients}
          </div>
          <div className="coffee-ingredients">
            <span style={{ fontSize: "15px" }}>
              <b>Price :</b>
            </span>{" "}
            <b>{props.price}</b>
          </div>
          {props.soldOut ? (
            <p
              style={{ marginTop: "5px", fontWeight: "bolder", color: "#222" }}
            >
              Sold out
            </p>
          ) : (
            <button
              className="button primary-button card-text"
              style={{ marginTop: "5px" }}
            >
              Order Now
            </button>
          )}
        </div>
      </div>
    );
  }
}

