import { React, useEffect, useState } from "react";

export default function Main() {
  const [key, setKey] = useState(null);

  function handleKeyPress(e) {
    setKey(e.code.key);
  }

  useEffect(function () {
    document.addEventListener("keypress", handleKeyPress);
  });

  return <p>{key}</p>;
}
