import React from "react";
import { useState } from "react";
import "./home.css";

export default function Counter2() {
  const [step, setStep] = useState(1);
  const [count, setCount] = useState(0);
  const date = new Date();
  date.setDate(date.getDate() + count);

  function decrementCount() {
    setCount((count) => {
      return count - step;
    });
  }

  function incrementCount() {
    setCount((count) => {
      return count + step;
    });
  }

  return (
    <div style={{ textAlign: "center" }}>
        <label htmlFor="step">step = {step} :</label>
        <input type="range" min={0} max={10} id="step" value={step} onChange={(e)=>{setStep(Number(e.target.value))}}></input>
      <br></br> <br></br>
      <button className="button primary-button" onClick={decrementCount}>
        --
      </button>
      <span> count : {count} </span>
      <button className="button primary-button" onClick={incrementCount}>
        +
      </button>
      <p>
        <span>
          {count === 0
            ? "Today is "
            : `${
                count > 0
                  ? count + " days from now is "
                  : Math.abs(count) + " days ago was "
              } `}
        </span>
        {date.toDateString()}
      </p>
      {(step !== 1 )}
    </div>
  );
}
