/* if (props.soldOut) {
  return null; soldOut is set to true for expresso,which means it will not be rendered on to UI 
} */
function Item(props) {
  let [isOrdered, setOrder] = useState(false);

  function set() {
    setOrder(!isOrdered);
  }

  return (
    <div className={`coffee-item ${props.soldOut ? "sold-out" : ""}`}>
      <img
        src="./assets/coffee_cup.png"
        alt={props.coffeeName}
        className="coffee-icon"
      ></img>
      <div className="coffee-name">{props.coffeeName}</div>
      <div className="coffee-ingredients">{props.ingredients}</div>
      <div style={{ display: "flex", justifyContent: "center" }}>
        {props.soldOut ? (
          <p style={{ marginTop: "5px", fontWeight: "bolder", color: "#222" }}>
            Sold out
          </p>
        ) : (
          <button
            className="button primary-button"
            style={{ marginTop: "5px" }}
            onClick={set}
          >
            Order Now
          </button>
        )}
      </div>
      {isOrdered && <Modal coffeeObj={props} />}
    </div>
  );
}

function Modal(props) {
  let [isRemoved, setRemoved] = useState(false);

  function remove() {
    setRemoved(!isRemoved);
  }

  return (
    <div className={`modal-item ${isRemoved ? "display-none" : ""}`}>
      <div className="cross">
        <button className="button cross-button" onClick={remove}>
          &times;
        </button>
      </div>
      <img
        src="./assets/coffee_cup.png"
        alt={props.coffeeObj.coffeeName}
        className="coffee-icon"
      ></img>
      <div className="modal-cont">
        <div className="coffee-name">
          <span style={{ fontSize: "1rem" }}>
            <b>Item-Name :</b>
          </span>{" "}
          {props.coffeeObj.coffeeName}
        </div>
        <div className="coffee-ingredients">
          <span style={{ fontSize: "1rem" }}>
            <b>Ingredients :</b>
          </span>{" "}
          {props.coffeeObj.ingredients}
        </div>
        <div className="coffee-ingredients">
          <span style={{ fontSize: "15px" }}>
            <b>About :</b>
          </span>{" "}
          Lorem ipsum dolor sit, amet consectetur adipisicing elit. Quas
          repellat cumque officia praesentium voluptates. Molestiae magni ipsum
          debitis asperiores officia dolorem quae quam odit nemo aut animi,
          consequuntur voluptate minus?
        </div>
      </div>
      <button className="button primary-button" style={{ marginTop: "5px" }}>
        Order now
      </button>
    </div>
  );
}

