import React from "react";

export default function Results({ words, time, count, position }) {
  let text = "";
  const wpm = Math.floor(words / time);
  const acc = Math.floor(count / position);
  if (wpm > 20 && acc > 75) {
    text =
      "Fantastic performance! You're typing faster and more accurately than ever before. This is an important milestone on your typing journey. Challenge yourself to hit 30 WPM and aim for 90% accuracy next. The sky's the limit!";
  }
  return (
    <div>
      <p>{words / time}</p>
      <p>{count / position}</p>
    </div>
  );
}
