import { React, useEffect, useState } from "react";
import KeyBoard from "./KeyBoard";

export default function Main() {
  const [position, setPositoin] = useState(0);
  const [count, setCount] = useState(0);
  const [time, setTime] = useState(0);
  //   const [selected, setSelected] = useState(113);

  const str = "I am Uday Kumar";

  useEffect(function () {
    function handleKeyPress(e) {
      if (position === 0) {
        const date = new Date();
        setTime(() => {
          const curTime =  date.getMinutes() + date.getSeconds() / 60;
          return curTime
        });
      }
      if (position === str.length-1) {
        const date = new Date();
        setTime((time) => {
          const curTime = date.getMinutes() + date.getSeconds() / 60;
          return (curTime - time).toFixed(3);
        });
      }

      if (e.keyCode === str.charCodeAt(position) && position < str.length) {
        console.log("matched");
        setCount((count) => count + 1);
      }
      setPositoin((position) => position + 1);
    }

    document.addEventListener("keypress", handleKeyPress);

    return function () {
      document.removeEventListener("keypress", handleKeyPress);
    };
  },[position,count]);

  return (
    <div>
      <p>{str}</p>
      <p>No of words matched : {count}</p>
      <p>Current position : {position}</p>
      {position === str.length && <p>Time taken : {time}</p>}
      <KeyBoard />
    </div>
  );
}
