import React from "react";

export default function DynamicResults({ count, position, words, time }) {
  const acc = Math.floor((count.current / position) * 100);
  const wpm = Math.floor(words / (time.current / 60000));
  return (
    <div className="dynamic" style={{display:"flex",alignItems:"center",}}>
      <p>{wpm}WPM</p>
      <p>{acc}%</p>
    </div>
  );
}
