import React from "react";
import ReactDOM from "react-dom/client";
import reportWebVitals from "./reportWebVitals";
import "./home.css";

const coffeeMenu = [
  { coffeeName: "Espresso", ingredients: "Espresso shot", soldOut: f },
  {
    coffeeName: "Latte",
    ingredients: "Espresso, Steamed Milk",
    soldOut: false,
  },
  {
    coffeeName: "Cappuccino",
    ingredients: "Espresso, Steamed Milk, Milk Foam",
    soldOut: false,
  },
  {
    coffeeName: "Macchiato",
    ingredients: "Espresso, Milk Foam",
    soldOut: true,
  },
  {
    coffeeName: "Mocha",
    ingredients: "Espresso, Chocolate Syrup, Steamed Milk, Whipped Cream",
    soldOut: false,
  },
  {
    coffeeName: "Flat White",
    ingredients: "Espresso, Microfoam",
    soldOut: false,
  },
  {
    coffeeName: "Americano",
    ingredients: "Espresso, Hot Water",
    soldOut: false,
  },
  {
    coffeeName: "Irish Coffee",
    ingredients: "Espresso, Whiskey, Sugar, Whipped Cream",
    soldOut: false,
  },
];

function App() {
  return (
    <>
      <Header />
      <Menu />
      <Footer />
    </>
  );
}

function Header() {
  return (
    <header>
      <div className="logo">Coffee Day</div>
      <div className="nav-buttons">
        <a className="button secondary-button" href="/">
          LOG IN
        </a>
        <a className="button primary-button " href="/">
          SIGN UP
        </a>
      </div>
    </header>
  );
}

function Menu() {
  const availableCoffees = coffeeMenu.length;
  return (
    <div className="container">
      {availableCoffees > 0 ? (
        <div className="coffee-grid">
          {coffeeMenu.map((ele) => (
            <Item
              coffeeName={ele.coffeeName}
              ingredients={ele.ingredients}
              soldOut={ele.soldOut}
            />
          ))}
        </div>
      ) : (
        <div
          className="coffee-item"
          style={{ width: "fit-content", marginInline: "auto" }}
        >
          We're still working on our menu.Please come back later...!
        </div>
      )}
    </div>
  );
}

/* alternate way for listing
{coffeeMenu.map((coffee) => (
  <Item coffeeObj={coffee} />
))} */

/* in the above code we have used (condition && <peice of jsx code>),
the jsx code will be a part of the UI only if the condition is satisfied.
This is because of the shortcircuiting property of the and(&&) operator
SHORT-CIRCUITING : and(&) operator returns the second element if the first argument is truthy-value(EX:numbers,true,non-empty object or variable) */

/* in the above code we can also a ternary operator,the advantage of using a ternary operator is it will provide 
an alternative to displayed whenever the condition fails */

/* component wise conditional renderingcan be done using if{}-else{} whereas peice wise conditional rendering 
is done using ternary operator or and operator */

/* Rendering Each element by passing props as arguments for each item,but it will be difficult when we deal with large data
<Item coffeeName="Espresso" ingredients="Espresso shot" />
Item coffeeName="Latte" ingredients="Espresso, Steamed Milk" />
<Item
  coffeeName="Cappuccino"
  ingredients="Espresso, Steamed Milk, Milk Foam"
/>
<Item coffeeName="Macchiato" ingredients="Espresso, Milk Foam" />
<Item
  coffeeName="Mocha"
  ingredients="Espresso, Chocolate Syrup, Steamed Milk, Whipped Cream"
/>
<Item
  coffeeName="Irish Coffee"
  ingredients="Espresso, Whiskey, Sugar, Whipped Cream"
/> */

/* if (props.soldOut) {
  return null; soldOut is set to true for expresso,which means it will not be rendered on to UI 
} */
function Item(props) {
  return (
    <div className={`coffee-item ${props.soldOut ? 'sold-out' : ''}`}>
      <img
        src="./assets/coffee_cup.png"
        alt={props.coffeeName}
        className="coffee-icon"
      ></img>
      <div className="coffee-name">{props.coffeeName}</div>
      <div className="coffee-ingredients">{props.ingredients}</div>
      <div style={{ display: "flex", justifyContent: "center" }}>
        {props.soldOut ? (
          <p style={{ marginTop: "5px", fontWeight: "bolder", color: "#222" }}>
            Sold out
          </p>
        ) : (
          <button
            className="button primary-button"
            style={{ marginTop: "5px" }}
          >
            Order now
          </button>
        )}
      </div>
    </div>
  );
}

function Footer() {
  // const hours = new Date().getHours();
  // const openingHor = 9;
  // const closingHour = 22;
  // const isOpen = hours >= openingHor && hours < closingHour;

  return (
    <div className="footer">
      <h3>Help-Line Details</h3>
      <p>contact: abc@gmail.com </p>
      <p>Tel: 123-456-6789</p>
      <p>Srikakulam ,Andhra Pradesh</p>
      <div className="icons">
        <p>
          <i class="ri-instagram-fill"></i>
        </p>
        <p>
          <i class="ri-facebook-circle-fill"></i>
        </p>
        <p>
          <i class="ri-discord-fill"></i>
        </p>
        <p>
          <i class="ri-twitter-fill"></i>
        </p>
      </div>
    </div>
  );
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
