import React from "react";
import ReactDOM from "react-dom/client";
import reportWebVitals from "./reportWebVitals";
import "./home.css";

const coffeeMenu = [
  { coffeeName: "Espresso", ingredients: "Espresso shot" },
  { coffeeName: "Latte", ingredients: "Espresso, Steamed Milk" },
  {
    coffeeName: "Cappuccino",
    ingredients: "Espresso, Steamed Milk, Milk Foam",
  },
  {
    coffeeName: "Mocha",
    ingredients: "Espresso, Chocolate Syrup, Steamed Milk, Whipped Cream",
  },
  { coffeeName: "Macchiato", ingredients: "Espresso, Milk Foam" },
  { coffeeName: "Flat White", ingredients: "Espresso, Microfoam" },
  { coffeeName: "Americano", ingredients: "Espresso, Hot Water" },
  {
    coffeeName: "Irish Coffee",
    ingredients: "Espresso, Whiskey, Sugar, Whipped Cream",
  },
  { coffeeName: "Affogato", ingredients: "Espresso, Vanilla Ice Cream" },
  {
    coffeeName: "Cold Brew",
    ingredients: "Cold Brew Coffee, Ice, Optional Milk or Syrup",
  },
];

function App() {
  return (
    <>
      <Header />
      <Menu />
    </>
  );
}

function Header() {
  return (
    <header>
      <div className="logo">Coffee Day</div>
      <div className="nav-buttons">
        <a className="button secondary-button" href="/">
          LOG IN
        </a>
        <a className="button primary-button " href="/">
          SIGN UP
        </a>
      </div>
    </header>
  );
}

function Menu() {
  return (
    <div className="container">
      <div className="coffee-grid">
        <Item coffeeName="Espresso" ingredients="Espresso shot" />
        <Item coffeeName="Latte" ingredients="Espresso, Steamed Milk" />
        <Item
          coffeeName="Cappuccino"
          ingredients="Espresso, Steamed Milk, Milk Foam"
        />
        <Item
          coffeeName="Mocha"
          ingredients="Espresso, Chocolate Syrup, Steamed Milk, Whipped Cream"
        />
        <Item coffeeName="Macchiato" ingredients="Espresso, Milk Foam" />
        <Item
          coffeeName="Irish Coffee"
          ingredients="Espresso, Whiskey, Sugar, Whipped Cream"
        />
      </div>
    </div>
  );
}

function Item(props) {
  return (
    <div className="coffee-item">
      <img src="./assets/coffee.png" alt="coffee" className="coffee-icon"></img>
      <div className="coffee-name">{props.coffeeName}</div>
      <div className="coffee-ingredients">{props.ingredients}</div>
      <div style={{display:"flex",justifyContent:"center"}}>
        <button className="button primary-button" style={{marginBlock:"au"}}>Order now</button>
      </div>
    </div>
  );
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
