import React from "react";

export default function FinalResults({ words, time, count, position }) {
  let text = "";
  const wpm = Math.floor(words / (time.current / 60000));
  const acc = Math.floor((count.current / position) * 100);
  document.querySelector(".circular-progress").style.setProperty("--accuracy", acc)
  if (wpm > 25 && acc > 75) {
    text = `Fantastic,this is an important milestone on your typing journey. Challenge yourself to hit 30 WPM and aim for 90% accuracy next. The sky's the limit!`;
  }
  return (
    <div>
      <div class="circular-progress">
        <div class="inner-circle">
          <span class="accuracy-value"></span>
          <p class="accuracy-text">real accuracy</p>
        </div>
      </div>
      <p class="label">accuracy</p>
    </div>
  );
}
